// this file will be for the navbar component
//! STEP 1: import react and styles
import "./index.scss";

// const Navigation = () => (<>Hello</>)
import { useState } from "react";
import LogoInit from "../../assets/images/logo-init.png";
import LogoWeb3 from "../../assets/images/logo-web3.png";
import { Link, NavLink } from "react-router-dom";

// import fontawesome icons
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faHome, faUser, faBriefcase, faEnvelope, faBars, faClose } from "@fortawesome/free-solid-svg-icons";
import { faLinkedin, faGithub } from "@fortawesome/free-brands-svg-icons";


//! STEP 2: create a function that returns the navbar component
const Navigation = () => { 
    const [navToggle, setNavToggle] = useState(false);

    return (  //TODO: add the logo images commented out below
    <div className="nav-bar">
        <Link className="logo" to="/" onClick={() =>setNavToggle(false)}>
        <img src={LogoInit} alt="Dev logo" />
        <img className="sub-logo" src={LogoWeb3} alt="Dev logo" />
        </Link>

{/* links to pages OK */}
        <nav className={navToggle? 'mobile-show' : ''}>
            <NavLink exact='true' activeClassName="active" to="/" onClick={() =>setNavToggle(false)}>
                <FontAwesomeIcon icon={faHome} color='#a52a2a' />
            </NavLink>

            <NavLink activeClassName="active" to="/about" className='about-link' onClick={() =>setNavToggle(false)}>
                <FontAwesomeIcon icon={faUser} />
            </NavLink>

            <NavLink activeClassName="active" to="/portfolio" className='portfolio-link' onClick={() =>setNavToggle(false)}>
                <FontAwesomeIcon icon={faBriefcase} />
            </NavLink>

            <NavLink activeClassName="active" to="/contact" className='contact-link'>
                <FontAwesomeIcon icon={faEnvelope} />
            </NavLink>
        </nav>

{/* liks to social media */}
        <ul>
            <li>
                <a href="https://www.linkedin.com/in/freddy-dordoni-7a57b213a/" target='_blank' rel='noreferrer'>
                     <FontAwesomeIcon icon={faLinkedin} color='#a52a2a' />
                </a>
            </li>
            <li>
                <a href="https://github.com/AussieKing" target='_blank' rel='noreferrer'>
                     <FontAwesomeIcon icon={faGithub} color='chocolate' />
                </a>
            </li>
        </ul>
        <FontAwesomeIcon 
          onClick={() => setNavToggle(true)}
          icon={faBars}
          color="#ffd700"
          size="3x"
          className='hamburger-icon' />
    </div>
    );
}


//! STEP 3: export the navbar component
export default Navigation;
