import React, { useEffect, useState } from "react";
import Loader from "react-loaders";
import AnimatedLetters from "../AnimatedLetters";
import "./index.scss";

const Portfolio = () => {
  const [letterClass, setLetterClass] = useState('text-animate');
  const [portfolio, setPortfolio] = useState([]);

  useEffect(() => {
    const timer = setTimeout(() => {
      setLetterClass('text-animate-hover');
    }, 3000);

    return () => {
      clearTimeout(timer);
    }
  }, []);

  return (
    <>
      <div className="container portfolio-page">
        <h1 className="page-title">
          <AnimatedLetters
            letterClass={letterClass}
            strArray={"Portfolio".split("")}
            idx={15}
          />
        </h1>
      </div>

      <div className="projects-container">
      <div className="project-item">
  <h2>Random Password Generator</h2>
  <p>
    Generates a random password based on user-selected criteria. This app will run
    in the browser and feature dynamically updated HTML and CSS powered by JavaScript code.
    It also features a clean and polished user interface and is responsive, ensuring
    that it adapts to multiple screen sizes.
  </p>
  <div style={{ width: "100%", height: 0, paddingBottom: "87%", position: "relative" }}>
    <iframe
      src="https://giphy.com/embed/B34skTNZtR7945bfmE"
      width="100%"
      height="100%"
      style={{ position: "absolute" }}
      frameBorder="0"
      class="giphy-embed"
      allowFullScreen
    ></iframe>
  </div>
  <p>
    <a href="https://giphy.com/gifs/password-generator-B34skTNZtR7945bfmE">via GIPHY</a>
  </p>
</div>

