// AnimatedLetters.js

import React from 'react';
import './AnimatedLetters.scss';

const AnimatedLetters = ({ strArray }) => {
  return (
    <span className="animated-letters">
      {strArray.map((char, i) => (
        <span key={i} className="animated-letter">
          {char}
        </span>
      ))}
    </span>
  );
};

export default AnimatedLetters;
