import { useEffect, useState } from 'react';
import Logo from '../../assets/images/logo.png';
import { Link } from 'react-router-dom';
import './index.scss';
import AnimatedLetters from '../AnimatedLetters';
import Navigation from '../Navigation';
import Loader from 'react-loaders';
import Footer from '../Footer';

const Home = () => {
  const [letterClass, setLetterClass] = useState('text-animate');

  const strArray1 = ['H', 'i', ',', '', 'I', "'", 'm', ' ', 'F', 'r', 'e', 'd', 'd', 'y'];
  const jobArray = ['Full Stack Developer'];
  const available = ['Available for hire'];

  useEffect(() => {
    return setTimeout(() => {
      setLetterClass('text-animate hover');
    }, 4000);
  }, []);

  return (
    <>
      <div className="container-home">
        <div className="text-area">
          <img src={Logo} alt="dev name" />
          <div className="text">
            <h1>
              <Navigation />
              <AnimatedLetters letterClass={letterClass} strArray={strArray1} index={1} />
            </h1>
            <h2>
              <AnimatedLetters letterClass={letterClass} strArray={jobArray} index={2} />
            </h2>
            <h2 className="available">
              <AnimatedLetters letterClass={letterClass} strArray={available} index={4} />
            </h2>
            <Link to="/contact" className="flat-button">
              Contact Me
            </Link>
          </div>
        </div>
        <Footer />
      </div>
      <Loader type="pacman" />
    </>
  );
};

export default Home;
