import React from 'react'
import { Container, Row, Col, Button } from 'react-bootstrap'
import './index.scss'
import PDF from '../../assets/images/Federico Dordoni CV resume 2023 Dev.pdf'
import Navigation from '../Navigation'
import AnimatedLetters from '../AnimatedLetters'
import { useEffect, useState } from 'react'
import Loader from 'react-loaders'

const Resume = () => {
  const [letterClass, setLetterClass] = useState('text-animate')

  useEffect(() => {
    return setTimeout(() => {
      setLetterClass('text-animate-hover')
    }, 3000)
  }, [])

  return (
    <div className="about-page">
      <Navigation />

      <Container>
        <Row>
          <Col md={9}>
            <div className="text-area">
              <h1>
                <AnimatedLetters
                  letterClass={letterClass}
                  strArray={['R', 'e', 's', 'u', 'm', 'e']}
                  index={15}
                />
              </h1>
              <div className="h2-container">
                <h2>Technical Skills</h2>
                <hr className="star-secondary" />

                  <h2>Languages:</h2>
                    <h3>JavaScript, HTML, CSS</h3> 

                  <li className="list-inline-item">
                    <h3>Computer Science applied to JavaScript:</h3>
                    Algorithms, Efficiency, Time Complexity, Big O Notation,
                    Data Structures
                  </li>
                  <li className="list-inline-item">
                    <h3>Libraries/Frameworks:</h3> React, jQuery,
                    Bootstrap, HTML, Bootstrap, Handlebars, Local/Session
                    Storage, IndexedDB
                  </li>
                  <li className="list-inline-item">
                    <h3>Server Side Development:</h3> Node.js,
                    Express.js, User Authentication, Progressive Web
                    Applications (PWAs), MERN Stack
                  </li>
                  <li className="list-inline-item">
                    <h3>API Interaction:</h3> API, JSON, AJAX, REST
                  </li>
                  <li className="list-inline-item">
                    <h3>Databases:</h3> MongoDB, MySQL
                  </li>
                  <li className="list-inline-item">
                    <h3>Quality Assurance:</h3> Unit Testing, Functional
                    Testing, Linting, Continuous Integration
                  </li>
                  <li className="list-inline-item">
                    <h3>Tools/Platforms:</h3> Git, GitHub, Heroku
                  </li>

              </div>

              <h2>Education</h2>
              <hr className="star-secondary" />
              <div className="resume-item">
                <h3>Diploma in Business Management</h3>
                <h4>Australian Pacific College, 2009-2012</h4>
                <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
              </div>

              <h2>Work Experience</h2>
              <hr className="star-secondary" />
              <div className="resume-item">
                <h3>Assistant Category Manager</h3>
                <h4>Endeavor Group Pty Ltd (Dan Murphy's), 2020-present</h4>
                <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
              </div>

              <h2>Proficiencies</h2>
              <ul>
                <h3>
                <li>HTML/CSS</li>
                <li>JavaScript</li>
                <li>
                  <span className="teal-color">M</span>
                  ongoDB
                </li>
                <li>
                  <span className="teal-color">E</span>xpress
                </li>
                <li>
                  <span className="teal-color">R</span>eact
                </li>
                <li>
                  <span className="teal-color">N</span>ode.js
                </li>
                <li>SQL</li>
                </h3>
              </ul>

              <hr className="star-primary" />
              <Button
                variant="dark"
                href={PDF}
                download
                className="download-resume"
              >
                Download Resume
              </Button>
            </div>
          </Col>
          <Col md={3}>
            <div></div>
          </Col>
        </Row>
        </Container>
      <Loader type="pacman" />
    </div>
  )
}

export default Resume

