import { useEffect, useState } from 'react'
import Logo from '../../assets/images/logo.png'
import { Link } from 'react-router-dom'
import './index.scss';
import AnimatedLetters from '../AnimatedLetters';

const Home = () => {
    const [letterClass, setLetterClass] = useState('text-animate')  // use the useState hook to set the state
    // create 2 arrays for the words to animate
    const strArray1 = ['F', 'r', 'e', 'd', 'd', 'y', ' ', 'D', 'o', 'r', 'd', 'o', 'n', 'i']   
    const jobArray = ['F', 'u', 'l', 'l', ' ', 'S', 't', 'a', 'c', 'k', ' ', 'W', 'e', 'b', ' ', 'D', 'e', 'v', 'e', 'l', 'o', 'p', 'e', 'r']

    useEffect(() => {  // use the useEffect hook to set the timeout
        return setTimeout(() => {
            setLetterClass('text-animate done')  // set the state to the class name with the animation
        }, 1000)
    }, [])  // pass an empty array as the second argument to the useEffect hook, so that it only runs once

    return (
        <div classname="container-home">
            <img src={Logo} alt='dev name' />
            <div classname="text-area">
                <h1>Hi, I'm </h1> 
                <h1>
                <AnimatedLetters letterClass={letterClass} strArray={strArray1} index={15} />  {/* pass the props to the component */}
                <AnimatedLetters letterClass={letterClass} strArray={jobArray} index={22} />  {/* pass the props to the component */}
                </h1> 
                <h2>Full Stack Web Developer</h2>
                <Link to='/contact' classname='flat-button'>Contact Me</Link>
            </div>
        </div>
    )
}

export default Home
